css:
.text-center {
    text-align: center;
    vertical-align: middle;
}

.e-invoice-table tr:nth-child(odd) {
    background-color: #f2f2f2;
}

.e-invoice-table tr:nth-child(even) {
    background-color: #ffffff;
}

.e-invoice-table td{
vertical-align:middle !important;
}
.e-invoice-table th{
    background-color: #ffffff;
}

.payment_terms_table th, .payment_terms_table td{
    word-wrap: break-word;
    white-space: normal;
    text-overflow: ellipsis;
}


.print-heading{
    border-bottom:1px solid #000;
    margin-bottom:0;
    padding: 0;
    color:#000;
    font-weight:bold;
}

.einv-det .col-xs-7{
    height:100%;
}

.print-format {
    padding: 5px;
    margin: 0 !important;
}

.print-format .row:not(.section-break) {
    margin-top: 0px !important;
}

.section-heading {
    margin-top: 3px;
    margin-bottom: 0px;
    font-weight: bold;
}

.party-details-table {
    table-layout: fixed;
    width: 100%;
}
.party-details-table td {
    vertical-align: top;
    padding: 8px;
}


.print-format h6{
    font-weight: bold;
    margin-top:0px;
    margin-bottom:0px;
}

.e-invoice-table {
    /*margin: 5px 0;*/
}

.item-det{
    padding:0px !important;
    font-size:0.8em;
    height:100%;
    width:100%;
}

table.table {
    border: 1px solid #000 !important;
    margin: 0;
    width: 100%;
}

.e-invoice-table th,
        .e-invoice-table td{
            text-align:center !important;
        }
.e-invoice-table th{
    font-weight:bold;
}
.e-invoice-table{
    padding:0;

    table-layout: fixed;
}

table td, table th {
    border: 1px solid #000 !important;
    white-space: normal;
    word-wrap: break-word;
}
.print-format td, .print-format th{
    padding:1px !important;
}

.qrcode {
    width: 125px;
}

.no-preview-available {
    color: grey;
    font-size: 14px;
}

.wrap-text {
    white-space: pre-wrap
}

.einv-det .col-xs-7.column-break.text-right {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
    font-size:10px;
}

.einv-det .col-xs-7.column-break.text-right .row.data-field {
    display: flex;
    align-items: center; 
    height: 100%;
    padding: 0;
    flex: 1; 
}

.einv-det .col-xs-7.column-break.text-right .row.data-field label, 
.einv-det .col-xs-7.column-break.text-right .row.data-field .value {
    padding: 2px;
    flex: 1; 
}
.print-format,table{
    font-size:0.7rem;
}

.watermark {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(-45deg);
    -webkit-transform:translate(-50%, -50%) rotate(-45deg);
    opacity: 0.075;
    font-size: 50px;
    color: #000;
    z-index: 1000;
    white-space: normal;
    pointer-events: none; 
}


#header-html {
    position: static;
    top: 0;
}
#footer-html{
    position: static;
    bottom: 0;
}

.page{
    /*background:rgba(0,0,0,0.1);*/
    /*padding:0 10px 0 10px;*/
    margin:0 0 0 0;
    padding:0 0 0 0;
}

.main-content {
    box-sizing: border-box;
    /*page-break-after: always;*/
}
.page-number {
    content: counter(page);
}

.no-break {
        page-break-inside: avoid;
    }









.borderless,
.borderless th,
.borderless td {
    border: none !important;
}

.print-format table td img.barcode,
.print-format table td img.qr-code {
    margin-top: 5px;
    width: 95px !important;
    image-rendering: -webkit-optimize-contrast;
}
.vehicle-info {
    margin: 0 !important;
    font-size: 9px;
    border-collapse: collapse;
}
.vehicle-info th {
    font-weight: bold;
}
.section-separator {
    border-top: 2px solid #bbb;
    margin: 8px 0 16px;
}
.ewb-no-span {
    font-size: 13px;
}
.vehicle-info td,
.vehicle-info th {
    border: 0 !important;
    border-top: 1px solid black !important;
}
.e-waybill-number {
    font-size: 16px;
}
h1.title {
    font-size: 18px;
    font-weight: bold;
    margin: 0;
}

tbody>tr>td,
tbody>tr>th {
    padding: 6px !important;
    border-top: none;
    color: black;
}

.ewaybill-page-layout {
    font-size: 12px;
    line-height: 1.4286;
    box-sizing: border-box;
    transform-origin: top center;
    width: 95%;
    margin: 0 auto;
}
