css:
.print-format {
    margin-top:37mm;
    margin-left: 2mm;
    margin-right: 2mm;
    /*height: 100% !important;*/
    /*margin-bottom:5mm;*/
}

.print-heading{
    border-bottom:1px solid #000;
    margin-bottom:-5mm;
    color:#000;
    font-weight:bold;
}

h6{
    font-weight: bold;
    margin-top:0px;
    margin-bottom:0px;
}

.no-border td {
  border: none;
  padding: 2px 4px;
}

.invoice-copy {
  display: block;
  font-size: 0.45rem;
  margin-top: 3px;
  margin-bottom: 6px;
}


.einv-det .col-xs-8.text-right .row.data-field {
  /*margin-bottom: 4mm;*/
  vertical-align: middle !important;
  margin-left:0;
  margin-right:0;
  margin-top: 4mm;
  margin-bottom:4mm;
}

.qrcode {
  width: 50mm;
  /*display: block;*/
}

.party-details-table {
    /*table-layout: fixed;*/
    width: 100%;
    margin-bottom:auto;
    padding-bottom:0;
}
.party-details-table td {
    vertical-align: top;
    /*padding: 8px;*/
}

.item-det{
    margin:0;
}
table{
    margin:0 !important;
}

.item-det td,.item-det th{
    text-align:center;
    font-size:0.8em;
    padding:0.2em !important;
    vertical-align: middle !important;
}

.item-det .even td{
    background-color: #f1f1f1;

}
.item-det .odd td {
    background-color: #ffffff;
}

.item-det .item-det-item-name{
    text-align:justify;
    font-size:1em;
    padding-left: 1mm !important;
    width:325px;
    font-size:0.85em;
}

.final-tax-total-table {
  page-break-inside: avoid;
  /*position: -webkit-sticky;*/
  /*position: fixed;*/
  margin-top:2mm;
  /*bottom:0;*/
  width:100%;
}

.no-preview-available {
    color: grey;
    font-size: 14px;
}

.page-footer-1 {
    content: counter(page);
    border-top:2px solid black;
    margin-top: -35mm !important;
    margin-bottom: -5mm !important;
}

#ewaybill-page-layout {
    font-size: 12px;
    width: 100%;
    margin: 0;
}

#ewaybill-page-layout table{
    font-size: 10px;
    line-height:1.1;
    width: 100%;
    margin: 0;
}

.print-format table td img.barcode,
.print-format table td img.qr-code {
    /*margin-top: 5px;*/
    width: 100px !important;
    image-rendering: -webkit-optimize-contrast;
}


.watermark {
  position: fixed;
  top: 150mm;             /* push to vertical middle */
  left: 0;            /* push slightly left */
  width: 100%;
  text-align: center;
  font-size: 90px;
  color: #000000;
  opacity: 0.04;        /* light watermark */
  -webkit-transform: rotate(-45deg);
  -ms-transform: rotate(-45deg);
  transform: rotate(-45deg);
  z-index: 1000;
}

html:
{%- from "templates/print_formats/standard_macros.html" import add_header, render_field, print_value -%}

{% set e_invoice_log = None %}
{% if doc.irn %}
{% set e_invoice_log = frappe.db.get_value(
        "e-Invoice Log", doc.irn, ("invoice_data", "signed_qr_code"), as_dict=True
    ) %}
{% set invoice_data = e_invoice_log and e_invoice_log.invoice_data and _dict(json.loads(e_invoice_log.invoice_data)) or None %}
{% endif %}

{% macro format_address(address) %}
{% if address %}
<p>
    {{ address.address_line1 }}
    {% if address.address_line2 %}
    {{ address.address_line2 }}<br>
    {% endif %}
    {{ address.city }}, {{ address.state }}, {{ address.country }}: {{ address.pincode }}<br>
</p>
{% endif %}
{% endmacro %}

{% set bns_settings = frappe.get_doc("BNS Settings") %}
{% set base_currency = frappe.db.get_value("Company", doc.company, "default_currency") %}


{% set company_cin = frappe.db.get_value("Company", doc.company, "bns_company_cin") %}
{% set msme_no = frappe.db.get_value("Company", doc.company, "bns_msme_no") %}
{% set msme_type = frappe.db.get_value("Company", doc.company, "bns_msme_type") %}

{% set copy_map = {
    "1": "Original for Recipient",
    "2": "Duplicate for Transporter",
    "3": "Duplicate for Supplier",
    "4": "Triplicate for Supplier"
} %}

{% set invoice_copy_param = frappe.form_dict.get("invoice_copy") or "1" %}
{% set invoice_copy_text = copy_map.get(invoice_copy_param, "Original for Recipient") %}


<!--<div class="watermark text-center">MEVABITE</div>-->

<!--<div class="page">-->


<div id="header-html">
    <div class="watermark">{{ invoice_copy_text }}</div>
    <div class="row print-heading" style="line-height:1.1;padding:10px">
        <div class="col-xs-3 column-break">
            {% set company_logo = frappe.db.get_value("Company", doc.company, "logo_for_printing") %}
            {% if company_logo %}
            <div>
                <img height="125px" width="150px" src="{{ company_logo }}">
            </div>
            {% endif %}
        </div>
        <div class="col-xs-6 column-break text-center">
            <h3 style="margin-bottom:0;"><b>{{ doc.company }}</b></h3>
            {% if frappe.db.get_value("Company",doc.company,"bns_previously_known_as") %}
                <div style="font-size:0.85em;">Previously Known As: {{frappe.db.get_value("Company",doc.company,"bns_previously_known_as")}}</div>
            {% endif %}
            <div style="font-size:1em">
                {% set seller_address = frappe.db.get_value("Address", doc.company_address, ["address_line1", "address_line2", "city", "state", "pincode", "country"], as_dict=True) %}
                    {{ format_address(seller_address) }}
                    {% if company_cin %}
                    <span style="font-size:0.95em;">CIN: {{ company_cin }}</span><br>
                    {% endif %}
                    {% if msme_no %}
                    <span style="font-size:0.95em;">MSME No.: {{ msme_no }}</span>
                    {% if msme_type %}
                    <span style="font-size:0.95em;"> | MSME Type: {{ msme_type }}</span>
                    {% endif %}
                    <br>
                    {% endif %}
                    {% if doc.company_gstin %}
                    <span style="font-size:0.95em;"><b>GST:</b> {{ doc.company_gstin }}</span> | 
                    {% endif %}
                    <span style="font-size:0.95em;">PAN: {{ frappe.db.get_value("Company", doc.company, "pan") }}</span><br>

            </div>
        </div>
        <div class="col-xs-3 column-break text-right">
            <h2 style="margin-top:5mm;"><b>
                    {% if doc.status == "Cancelled" and doc.is_return %}
                        Cancelled Credit Note
                    {% elif doc.status == "Cancelled" %}
                        Cancelled Invoice
                    {% elif doc.status == "Draft" and doc.is_return %}
                        Draft Credit Note
                    {% elif doc.status == "Draft" %}
                        Draft Invoice
                    {% elif doc.is_return %}
                        Credit Note
                    {% elif doc.irn %}
                        e-Invoice
                    {% else %}
                        Tax Invoice
                    {% endif %}
                </b>
            </h2>
            <div class="invoice-meta">
              <table class="table-condensed no-border" style="width:100%; font-size:0.9em; text-align:left;">
                <tr>
                  <td><b>Invoice Date:</b></td>
                  <td>{{ frappe.utils.formatdate(doc.posting_date) }}</td>
                </tr>
                <tr>
                  <td><b>Invoice No:</b></td>
                  <td><b>{{ doc.name }}</b></td>
                </tr>
              </table>
            </div>
        </div>
    </div>
</div>

{% if doc.irn %}
<div class="einv-det">
    <!--<div class="col-xs-2 column-break text-left"></div>-->
    <div class="col-xs-4 column-break text-left">
        {% if e_invoice_log and e_invoice_log.signed_qr_code %}
        <img class="qrcode" src="data:image/png;base64,{{ get_qr_code(e_invoice_log.signed_qr_code, scale=2) }}">
        {% endif %}
    </div>
    <div class="col-xs-8 column-break text-right">
        <div class="row data-field">
            <div class="col-xs-4"><label>IRN</label></div>
            <div class="col-xs-8 value"><u>{{ doc.irn }}</u></div>
        </div>
        {% if invoice_data and invoice_data.AckNo %}
        <div class="row data-field">
            <div class="col-xs-4"><label>Ack. No.</label></div>
            <div class="col-xs-8 value">{{ invoice_data.AckNo }}</div>
        </div>
        {% endif %}
        {% if invoice_data and invoice_data.AckDt %}
        <div class="row data-field">
            <div class="col-xs-4"><label>Ack. Date</label></div>
            <div class="col-xs-8 value">{{ frappe.utils.format_datetime(invoice_data.AckDt, "dd/MM/yyyy hh:mm:ss") }}</div>
        </div>
        {% endif %}
        {% if doc.ewaybill %}
        <div class="row data-field">
            <div class="col-xs-4"><label>e-Waybill No.</label></div>
            <div class="col-xs-8 value"><u>{{ doc.ewaybill }}</u></div>
        </div>
        {% endif %}
    </div>
    <!--<div class="col-xs-1 column-break text-right">-->
    </div>
{% endif %}

<table class="table table-bordered party-details-table">
    <tbody>
        <tr>
            <td colspan=3 rowspan=4 style="width:45%;">
                {% set contact_person = frappe.db.get_value(
            "Contact", doc.contact_person, ("mobile_no"), as_dict=True
        ) %}
                <h6><b><u>Buyer Details</u></b></h6>
                <p><b>{{ doc.customer_name }}</b></p>
                {% set customer_address = frappe.db.get_value("Address", doc.customer_address, ["address_line1", "address_line2", "city", "state", "pincode", "country","gstin"], as_dict=True) %}
                {{ format_address(customer_address) }}

                {% if doc.billing_address_gstin %}
                GST: <u>{{ doc.billing_address_gstin }}</u><br>
                {% endif %}
                {% set customer_pan = frappe.db.get_value("Customer", doc.customer_name, "pan")%}
                {% if customer_pan %}
                PAN: {{ customer_pan }}<br>
                {% endif %}
                {% if contact_person.mobile_no %}
                Phone No: {{ contact_person.mobile_no }}
                {% endif %}
                {% if doc.shipping_address %}
                <br>
                <h6><b><u>Shipping Details</u></b></h6>
                {% set shipping_address = frappe.db.get_value("Address", doc.shipping_address_name, ["address_line1", "address_line2", "city", "state", "pincode", "country","gstin"], as_dict=True) %}
                {{ format_address(shipping_address) }}
                {% if shipping_address.gstin %}
                GST: <u>{{ shipping_address.gstin }}</u><br>
                {% endif %}
                {% endif %}
                {% if doc.dispatch_address %}
                <h6><b><u>Dispatch Details</u></b></h6>
                <p><b>{{ frappe.db.get_value("Address", doc.dispatch_address_name, "address_title") }}</b></p>
                <!--<p>{{doc.dispatch_address}}</p>-->
                {% set dispatch_address = frappe.db.get_value("Address", doc.dispatch_address_name, ["address_line1", "address_line2", "city", "state", "pincode", "country"], as_dict=True) %}
                {{ format_address(dispatch_address) }}
                {% endif %}
            </td>
            <td colspan="{{ 2 if (not doc.irn and doc.ewaybill) else 1 }}">
                <h6>Transporter:</h6>
                {% if doc.transporter_name %}
                {{ doc.transporter_name }} <br>
                {{ doc.gst_transporter_id }}
                {% else %}<br>{% endif %}
            </td>
            <td colspan=1>
                <h6>Vehicle No:</h6>
                {% if doc.vehicle_no %}
                {{ doc.vehicle_no }}
                {% else %}<br>{% endif %}
            </td>
            <td colspan=1>
                <h6>Builty No:</h6>
                {% if doc.custom_builty_no %}
                {{ doc.custom_builty_no }}
                {% else %}<br>{% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=1>
                <h6>Place Of Supply</h6>
                {{ doc.place_of_supply }}
            </td>
            {% if not doc.irn and doc.ewaybill %}
            <td colspan=1>
                <h6>e-Waybill No.</h6>
                <u>{{ doc.ewaybill }}</u>
            </td>            
            {% endif %}
            <td colspan=1>
                <h6>Buyer's PO No:</h6>
                {% if doc.po_no %}
                {{ doc.po_no }}
                {% else %}<br>{% endif %}
            </td>
            <td colspan=1>
                <h6>PO Date:</h6>
                {% if doc.po_date %}
                {{ doc.po_date }}
                {% else %}<br>{% endif %}
            </td>
        </tr>
        {% if doc.incoterm or doc.named_place  %}
        <tr>
            <td colspan="{{ 2 if (not doc.irn and doc.ewaybill) else 1 }}">
                <h6>Incoterm:</h6>
                {% if doc.incoterm %}
                {{ doc.incoterm }}
                {% else %}<br>{% endif %}
            </td>
            <td colspan=2>
                <h6>Named Place:</h6>
                {% if doc.named_place %}
                {{ doc.named_place }}
                {% else %}<br>{% endif %}
            </td>
        </tr>
        {% endif %}
        <tr>
            <td colspan=1>

                <h6>Destination:</h6>
                {% if doc.custom_destination %}
                {{doc.custom_destination}}
                {% else %}<br>{% endif %}
            </td>
            <td rowspan=2 colspan="{{ 3 if (not doc.irn and doc.ewaybill) else 2 }}">
                <h6>Terms of Delivery:</h6>
                {% if doc.custom_terms_of_delivery %}
                {{ doc.custom_terms_of_delivery }}
                {% elif doc.custom_terms_of_delivery_new %}
                {{ doc.custom_terms_of_delivery_new }}
                {% else %}<br><br>{% endif %}
            </td>
        </tr>
        <tr>
            <td colspan=1 rowspan=1>
                <h6>Doc No:</h6>
                {% if doc.custom_doc_no %}
                {{doc.custom_doc_no}}
                {% else %}<br>{% endif %}
            </td>
        </tr>
    </tbody>
</table>


<table class="table table-bordered item-det">
<thead>
    <tr>
        <th colspan="2">#</th>
        <th>Item</th>
        <th colspan="7">Qty</th>
        {% if bns_settings.rate_excl_tax %}
        <th colspan="9">Rate(Exc Tax)</th>
        {% endif %}
        {% if doc.is_print_gst_rate_per_row %}
        <th colspan="4">GST</th>
        {% endif %}
        {% if bns_settings.rate_incl_tax %}
        <th colspan="9">Rate(Inc Tax)</th>
        {% endif %}
        {% if bns_settings.discount_type == "Triple Compounded" %}
        <th colspan="4">D1</th>
        <th colspan="4">D2</th>
        <th colspan="4">D3</th>
        {% else %}
        <th colspan="4">Disc</th>
        {% endif %}
        {% if bns_settings.is_item_amount_tax_incl %}
        <th colspan="11">Amount(Incl)</th>
        {% else %}
        <th colspan="11">Amount(Excl)</th>
        {% endif %}
    </tr>
</thead>

    <tbody>
        {% for item in doc.items %}
        <tr class="no-break {{ 'odd' if loop.index is odd else 'even' }}">
            <td colspan="2">{{ loop.index }}</td>
            <td class="item-det-item-name">
                <b>{% if bns_settings.show_item_code or doc.bns_show_item_code %}{{ item.item_code }}:{% endif %}{{ item.item_name }}</b><br>
                <p style="font-size:0.8em;margin:0;"><i>
                {% if item.gst_hsn_code %}
                    <b>HSN/SAC: </b>{{item.gst_hsn_code}}
                {% endif %}
                {% if item.barcode and (doc.bns_show_item_barcode or bns_settings.bns_show_item_barcode) %}
                    | <b>EAN:</b> {{ item.barcode }}
                {% endif %}
                </i>
                </p>
            </td>
            <td colspan="7">
                <b>
                    <span>{{ item.qty }} {{ item.uom }}</span>
                    {% if bns_settings.secondary_rate_display %}
                        {% if bns_settings.secondary_rate_field == "print_uom" %}
                            {% set print_uom = frappe.db.get_value("Item", item.item_code, "custom_print_uom") %}
                            {% if print_uom and item.uom != print_uom %}
                                {% set print_uom_conversion = frappe.db.get_value(
                                    "UOM Conversion Detail",
                                    {"parent": item.item_code, "uom": print_uom},
                                    "conversion_factor"
                                ) %}
                                <br>
                                <small style="font-size:0.9em;">{{ (item.conversion_factor * item.qty / print_uom_conversion ) | int }} {{ print_uom }} </small>

                            {% endif %}
                        {% elif bns_settings.secondary_rate_field == "weight_uom" and item.total_weight != 0 %}
                            {% if item.weight_uom == "Kg" and item.weight_uom != item.uom %}
                                <br>
                                <small style="font-size:0.75em;">{{ item.total_weight }} Kg</small>
                            {% elif item.weight_uom == "Gram" and item.weight_uom != "Kg" and item.weight_uom != "Gram" %}
                                <br>
                                <small style="font-size:0.85em;">{{ item.total_weight / 1000 }} Kg</small>
                            {% endif %}
                        {% endif %}
                    {% endif %}
                </b>
            </td>
            {% if doc.taxes[0].included_in_print_rate == 1 %}

                {% set gst_rate = frappe.db.get_value("Item Tax Template",{"name": item.item_tax_template},["gst_rate"]) %}
                {% set inclusive_rate = item.base_price_list_rate %}
                {% set exclusive_rate = inclusive_rate*100/(100+gst_rate) %}

            {% else %}

                {% set gst_rate = frappe.db.get_value("Item Tax Template",{"name": item.item_tax_template},["gst_rate"]) %}
                {% set exclusive_rate = item.base_price_list_rate %}
                {% set inclusive_rate = exclusive_rate*(100+gst_rate)/100 %}

            {% endif %}

            {% if bns_settings.rate_excl_tax %}
            <td colspan="9">
                <b>
                    <span>
                    {{ frappe.utils.fmt_money("{:.2f}".format(exclusive_rate), currency=base_currency) }}/<small style="font-size:0.45rem;">{{ item.uom }}</small>
                    </span>

                    {% if bns_settings.secondary_rate_display %}
                        {% if bns_settings.secondary_rate_field == "print_uom" %}
                            {% set print_uom = frappe.db.get_value("Item", item.item_code, "custom_print_uom") %}
                            {% if print_uom and item.uom != print_uom %}
                                {% set print_uom_conversion = frappe.db.get_value(
                                    "UOM Conversion Detail",
                                    {"parent": item.item_code, "uom": print_uom},
                                    "conversion_factor"
                                ) %}
                                <br>
                                <span style="font-size:0.95em;">
                                {{ frappe.utils.fmt_money("{:.2f}".format(exclusive_rate * print_uom_conversion / item.conversion_factor), currency=base_currency) }}/<small style="font-size:0.35rem;">{{ print_uom }}</small>
                                </span>
                            {% endif %}
                        {% elif bns_settings.secondary_rate_field == "weight_uom" and item.total_weight != 0 %}
                            {% if item.weight_uom == "Kg" and item.weight_uom != item.uom %}
                                <p style="font-size:0.85em;">
                                    {% set calculated_value = exclusive_rate * item.qty / item.total_weight %}
                                    {{ frappe.utils.fmt_money(calculated_value, currency=base_currency) }}/Kg
                                </p>
                            {% elif item.weight_uom == "Gram" and item.weight_uom != "Kg" and item.weight_uom != "Gram" %}
                                <p style="font-size:0.85em;">
                                    {% set calculated_value = exclusive_rate * item.qty * 1000 / item.total_weight %}
                                    {{ frappe.utils.fmt_money(calculated_value, currency=base_currency) }}/Kg
                                </p>
                            {% endif %}
                        {% endif %}
                    {% endif %}
                </b>
            </td>
            {% endif %}

            {% if doc.is_print_gst_rate_per_row %}
                <td colspan="4">{{ gst_rate | round(0) | int }}%</td>
            {% endif %}

            {% if bns_settings.rate_incl_tax %}
            <td colspan="9">
                <b>
                    <span>
                    {{ frappe.utils.fmt_money("{:.2f}".format(inclusive_rate), currency=base_currency) }}/<small style="font-size:0.45rem;">{{ item.uom }}</small>
                    </span>

                    {% if bns_settings.secondary_rate_display %}
                        {% if bns_settings.secondary_rate_field == "print_uom" %}
                            {% set print_uom = frappe.db.get_value("Item", item.item_code, "custom_print_uom") %}
                            {% if print_uom and item.uom != print_uom %}
                                {% set print_uom_conversion = frappe.db.get_value(
                                    "UOM Conversion Detail",
                                    {"parent": item.item_code, "uom": print_uom},
                                    "conversion_factor"
                                ) %}
                                <br>
                                <span style="font-size:0.9em;">
                                {{ frappe.utils.fmt_money("{:.2f}".format(inclusive_rate * print_uom_conversion / item.conversion_factor), currency=base_currency) }}/<small style="font-size:0.35rem;">{{ print_uom }}</small>
                                </span>
                            {% endif %}
                        {% elif bns_settings.secondary_rate_field == "weight_uom" and item.total_weight != 0 %}
                            {% if item.weight_uom == "Kg" and item.weight_uom != item.uom %}
                                <p style="font-size:0.8em;">
                                    {% set calculated_value = inclusive_rate * item.qty / item.total_weight %}
                                    {{ frappe.utils.fmt_money(calculated_value, currency=base_currency) }}/Kg
                                </p>
                            {% elif item.weight_uom == "Gram" and item.weight_uom != "Kg" and item.weight_uom != "Gram" %}
                                <p style="font-size:0.8em;">
                                    {% set calculated_value = inclusive_rate * item.qty * 1000 / item.total_weight %}
                                    {{ frappe.utils.fmt_money(calculated_value, currency=base_currency) }}/Kg
                                </p>
                            {% endif %}
                        {% endif %}
                    {% endif %}
                </b>
            </td>
            {% endif %}

            {# --- Step 1: Calculate / Fix discount_percentage if needed --- #}
            {% if item.discount_percentage == 0 and item.base_price_list_rate and item.base_rate and item.base_price_list_rate != item.base_rate %}
                {% set manual_discount = ((item.base_price_list_rate - item.base_rate) * 100) / item.base_price_list_rate %}
                {% set item_discount = "{:.2f}".format(manual_discount|float) %}
            {% else %}
                {% set item_discount = item.discount_percentage %}
            {% endif %}

            {# --- Step 2: Render depending on discount_type --- #}
            {% if bns_settings.discount_type != "Single" %}
                <td colspan="4" style="font-size:0.8em;">
                    {{ '-' if (item.custom_d1_ == 0.0 and not item_discount) else (item.custom_d1_ if item.custom_d1_ != 0.0 else item_discount) ~ '%' }}
                </td>
                <td colspan="4" style="font-size:0.8em;">
                    {{ '-' if item.custom_d2_ == 0.0 else item.custom_d2_ ~ '%' }}
                </td>
                <td colspan="4" style="font-size:0.8em;">
                    {{ '-' if item.custom_d3_ == 0.0 else item.custom_d3_ ~ '%' }}
                </td>
            {% else %}
                <td colspan="4">{{ item_discount }}%</td>
            {% endif %}


            {% if bns_settings.is_item_amount_tax_incl %}
            <td colspan="11"><b>{{ frappe.utils.fmt_money("{:.2f}".format(item.base_net_amount*(1+(gst_rate/100))), currency=base_currency) }}</b></td>
            {% else %}
            <td colspan="11"><b>{{ frappe.utils.fmt_money("{:.2f}".format(item.base_net_amount), currency=base_currency) }}</b></td>
            {% endif %}
        </tr>
        {% endfor %}
    </tbody>
</table>

<div class="final-tax-total-table">
    <table class="table table-bordered">
        <tbody>
            <tr>
                <td rowspan=1 colspan=5 style="vertical-align:top !important; width:45%;" class="text-center"><b>Bank Details</b></td>
                <td class="text-right" colspan=2><b>Total Taxable Value</b></td>
                <td class="text-right text-nowrap" colspan=2>
                    {{ frappe.utils.fmt_money("{:.2f}".format(doc.base_net_total), currency=base_currency) }}
                </td>
            </tr>

            {# --- calculate extra rows count --- #}
            {% set taxes_count = doc.taxes | length %}
            {% set extra_rows = taxes_count %}
            {% if doc.discount_amount %}
                {% set extra_rows = extra_rows + 1 %}
            {% endif %}
            {# +1 for Grand Total, +1 for Amount in words #}
            {% set total_rows = extra_rows + 2 %}

            <tr>
                <td class="text-center" rowspan={{ total_rows+1 }} colspan=5 style="vertical-align:top !important;">
                    {% set bank_account = frappe.db.get_value("Bank Account", {"company": doc.company, "is_default": 1}, ["account_name","bank", "bank_account_no", "branch_code","iban"], as_dict=True) %}
                    {% if bank_account.account_name %}
                    Account Name: <b><i>{{ bank_account.account_name }}</i></b><br>
                    {% endif %}
                    {% if bank_account.bank %}
                    Bank Name: <b><i>{{ bank_account.bank }}</i></b><br>
                    {% endif %}
                    {% if bank_account.bank_account_no %}
                    Account Number: <b><i>{{ bank_account.bank_account_no }}</i></b><br>
                    {% endif %}
                    {% if bank_account.branch_code %}
                    Branch Code (IFSC): <b><i>{{ bank_account.branch_code }}</i></b><br>
                    {% endif %}
                    {% if bank_account.iban %}
                    IBAN: <b><i>{{ bank_account.iban }}</i></b><br>
                    {% endif %}
                </td>
            </tr>

            {# --- loop through Sales Taxes & Charges rows --- #}
            {% for tax_row in doc.taxes %}
            <tr>
                <td class="text-right" colspan=2 style="font-size:0.8em;">
                    <b>{{ tax_row.description or tax_row.account_head }}</b>
                </td>
                <td class="text-right text-nowrap" colspan=2>
                    {{ frappe.utils.fmt_money("{:.2f}".format(tax_row.base_tax_amount), currency=base_currency) }}
                </td>
            </tr>
            {% endfor %}

            {% if doc.discount_amount %}
            <tr>
                <td class="text-right" colspan=2>Additional Discount</td>
                <td class="text-right text-nowrap" colspan=2>
                    <b>{{ frappe.utils.fmt_money(doc.base_discount_amount, currency=base_currency) }}</b>
                </td>
            </tr>
            {% endif %}

            {% set total_to_show = doc.base_rounded_total
            if doc.base_rounded_total and doc.base_rounded_total != 0
            else doc.base_grand_total
            %}

            <tr>
                <td class="text-right" colspan=2><b>Grand Total</b></td>
                <td class="text-right text-nowrap" colspan=2>
                    <b>{{ frappe.utils.fmt_money(total_to_show, currency=base_currency) }}</b>
                </td>
            </tr>
            <tr>
                <td colspan=4>
                    <b>Amount (in words): </b><span style="font-size:0.85em">{{ doc.base_in_words }}</span>
                </td>
            </tr>
        </tbody>
    </table>

    {{ doc.gst_breakup_table }}
    {% if doc.is_print_gst_table %}

    {% set gst_summary = {} %}
    {% set gst_flags = {"uses_igst": false, "uses_cgst_sgst": false} %}

        {% for item in doc.items %}
            {% set gst_rate = (frappe.db.get_value("Item Tax Template", {"name": item.item_tax_template}, "gst_rate") or 0) | float %}
            {% set key = gst_rate|string %}

            {% if item.igst_amount %}
                {% set _ = gst_flags.update({"uses_igst": true}) %}
                {% if gst_summary[key] is not defined %}
                    {% set _ = gst_summary.update({ key: {
                        "gst_rate": gst_rate,
                        "taxable_value": 0,
                        "igst_amount": 0
                    } }) %}
                {% endif %}
                {% set _ = gst_summary[key].update({
                    "taxable_value": gst_summary[key].taxable_value + item.net_amount,
                    "igst_amount": gst_summary[key].igst_amount + item.igst_amount
                }) %}
            {% elif item.cgst_amount and item.sgst_amount %}
                {% set _ = gst_flags.update({"uses_cgst_sgst": true}) %}
                {% if gst_summary[key] is not defined %}
                    {% set _ = gst_summary.update({ key: {
                        "gst_rate": gst_rate,
                        "taxable_value": 0,
                        "cgst_amount": 0,
                        "sgst_amount": 0
                    } }) %}
                {% endif %}
                {% set _ = gst_summary[key].update({
                    "taxable_value": gst_summary[key].taxable_value + item.net_amount,
                    "cgst_amount": gst_summary[key].cgst_amount + item.cgst_amount,
                    "sgst_amount": gst_summary[key].sgst_amount + item.sgst_amount
                }) %}
            {% endif %}
        {% endfor %}

        <h4>GST Summary</h4>
        <table class="table table-bordered table-hover" style="text-align: center;">
            <thead>
                <tr>
                    <th rowspan="2" style="text-align: center; vertical-align: middle;">GST RATE</th>
                    <th rowspan="2" style="text-align: center; vertical-align: middle;">Taxable Value</th>
                    {% if gst_flags.uses_cgst_sgst %}
                        <th colspan="2" style="text-align: center; vertical-align: middle;">CGST</th>
                        <th colspan="2" style="text-align: center; vertical-align: middle;">SGST/UTGST</th>
                    {% elif gst_flags.uses_igst %}
                        <th colspan="2" style="text-align: center; vertical-align: middle;">IGST</th>
                    {% endif %}
                </tr>
                <tr>
                    {% if gst_flags.uses_cgst_sgst %}
                        <th style="text-align: center; vertical-align: middle;">Rate</th>
                        <th style="text-align: center; vertical-align: middle;">Amount</th>
                        <th style="text-align: center; vertical-align: middle;">Rate</th>
                        <th style="text-align: center; vertical-align: middle;">Amount</th>
                    {% elif gst_flags.uses_igst %}
                        <th style="text-align: center; vertical-align: middle;">Rate</th>
                        <th style="text-align: center; vertical-align: middle;">Amount</th>
                    {% endif %}
                </tr>
            </thead>
            <tbody>
                {% for row in gst_summary.values() %}
                    <tr>
                        <td>{{ row.gst_rate }}%</td>
                        <td>{{ frappe.utils.fmt_money("{:.2f}".format(row.taxable_value), currency=doc.currency) }}</td>

                        {% if gst_flags.uses_cgst_sgst %}
                            <td>{{ (row.gst_rate / 2) | round(1) }}%</td>
                            <td>{{ frappe.utils.fmt_money("{:.2f}".format(row.cgst_amount), currency=doc.currency) }}</td>
                            <td>{{ (row.gst_rate / 2) | round(1) }}%</td>
                            <td>{{ frappe.utils.fmt_money("{:.2f}".format(row.sgst_amount), currency=doc.currency) }}</td>
                        {% elif gst_flags.uses_igst %}
                            <td>{{ row.gst_rate }}%</td>
                            <td>{{ frappe.utils.fmt_money("{:.2f}".format(row.igst_amount), currency=doc.currency) }}</td>
                        {% endif %}
                    </tr>
                {% endfor %}
            </tbody>
        </table>


    {% endif %}



    <table class="table table-bordered" style=" table-layout: fixed; border-collapse: collapse; width:100%; text-align:center; font-size:12px;">
        <tr rowspan=1>
            <td rowspan=1 colspan=4>
                <b>{% if doc.is_print_payment_terms==1%} Payment Terms {% endif %}</b>
            </td>
            <td colspan=4>
                <b>Pre Authenticated By: {{doc.company}}</b>
            </td>
        </tr>
        <tr>
            <td colspan=4 rowspan=3>
            {% if doc.payment_schedule and doc.is_print_payment_terms==1 %}
            <table class="payment_terms_table" style="width: 100%; border-collapse: collapse;">
                <thead>
                    <tr>
                        <th style="border: 1px solid black; padding: 8px;"><b>Due Date</b></th>
                        <th style="border: 1px solid black; padding: 8px;"><b>Invoice Portion</b></th>
                        <th style="border: 1px solid black; padding: 8px;"><b>Payment Amount</b></th>

                    </tr>
                </thead>
                <tbody>
                    {% for row in doc.payment_schedule %}
                    <tr>
                        <td style="border: 1px solid black; padding: 8px;">{{ row.due_date }}</td>
                        <td style="border: 1px solid black; padding: 8px;">{{ row.invoice_portion }}%</td>
                        <td style="border: 1px solid black; padding: 8px;">{{ frappe.utils.fmt_money(row.payment_amount, currency="INR") }}</td>
                    </tr>
                    {% endfor %}
                </tbody>
            </table>
            {% endif %}
            </td>
            <td colspan=4>
                Issuing Signatory
            </td>
        </tr>
        <tr rowspan=1>
            <td colspan=1 style="text-align:left">
                Name:
            </td>
            <td colspan=3></td>
        </tr>
        <tr rowspan=1>
            <td colspan=1 style="text-align:left;">
                Designation:
            </td>
            <td colspan=3></td>
        </tr>
    </table>
    <table class="table table-bordered">
        <tr>
            <td>
                {% if doc.terms %}

                    <h6 style="margin:0px;">Terms & Conditions:</h6>
                    {{ doc.terms }}

                {% endif %}
            </td>
        </tr>

    </table>

</div>


